package nl.progaia.esbxref;import java.util.Collections;import java.util.Iterator;import javax.xml.namespace.NamespaceContext;public class XQNamespaceContext implements NamespaceContext {	static final String PREFIX = "xq";	static final String NAMESPACE = "http://www.sonicsw.com/sonicxq";	public String getNamespaceURI(String prefix) {		if (PREFIX.equals(prefix))			return NAMESPACE;		return null;	}	public String getPrefix(String namespaceURI) {		if (NAMESPACE.equals(namespaceURI))			return PREFIX;		return null;	}	public Iterator<String> getPrefixes(String namespaceURI) {		if (NAMESPACE.equals(namespaceURI))			return Collections.singletonList(PREFIX).iterator();		return null;	}}